type Point =
    Point of int * int

type Move =
    Move of int * int

let slide (Point(x,_)) (Move(dx,_)) (slope: List<string> ) =
    (* Assuming that dy is 1 *)
    let width = slope.[0].Length
    slope |>
    List.fold 
        (fun (trees,x) s -> 
            match s.[x] with
            | '#' -> (trees+1, (x+dx) % width)
            | _ -> (trees, (x+dx) % width)
        )
        (0, 0)
    |> fst

let result1 =
    data
    |> List.ofArray
    |> slide (Point(0,0)) (Move(3,1))
    
